				Films.Add(new Film { Film_Name = "Безумный Макс: Дорога ярости", Film_director = "Джордж Миллер", Film_genre = "боевик, приключения, фантастика", Year_of_issue = "2015", poster = "~/Image/Mad Max Fury Road.jpg", Short_description = "Вскоре после отмщения за смерть жены и сына, Макс Рокатански покинул ряды «Основного силового патруля» и уехал в глушь, где скитается в одиночестве, пока мир медленно падает в последствии нефтяного кризиса и глобальной войны. Не имея ничего, кроме своей машины «Перехватчик», Максу предстоит научиться, как выжить в пост-апокалиптической пустоши и сражаться с жестокими, безжалостными воинами, которые населяют её. Преследуемый призраками беспокойного прошлого Макс уверен, что лучший способ выжить - скитаться в одиночестве. Несмотря на это, он присоединяется к бунтарям, бегущим через всю пустыню на боевой фуре, под предводительством отчаянной Фуриосы. Они сбежали из Цитадели, страдающей от тирании Несмертного Джо, и забрали у него кое-что очень ценное. Разъярённый диктатор бросает все свои силы в погоню за мятежниками, ступая на тропу войны - дорогу ярости." });
				Films.Add(new Film { Film_Name = "Зеленая миля", Film_director = "Фрэнк Дарабонт", Film_genre = "драма, фэнтези, криминал", Year_of_issue = "1999", poster = "~/Image/The Green Mile.jpg", Short_description = "Пол Эджкомб — начальник блока смертников в тюрьме «Холодная гора», каждый из узников которого однажды проходит «зеленую милю» по пути к месту казни. Пол повидал много заключённых и надзирателей за время работы. Однако гигант Джон Коффи, обвинённый в страшном преступлении, стал одним из самых необычных обитателей блока." });
                 
