				Films.Add(new Film { Film_Name = "Матрица", Film_director = "Лана Вачовски, Лилли Вачовски", Film_genre = "фантастика, боевик", Year_of_issue = "1999", poster = "~/Image/The Matrix.jpg", Short_description = "Жизнь Томаса Андерсона разделена на две части: днём он — самый обычный офисный работник, получающий нагоняи от начальства, а ночью превращается в хакера по имени Нео, и нет места в сети, куда он бы не смог проникнуть. Но однажды всё меняется. Томас узнаёт ужасающую правду о реальности." });
